import React from "react";

// reactstrap components
import {
  Container,
  Button,
  Row,
  Col
} from "reactstrap";

// core components

function ProductListElements() {

  const [showQuote, setShowQuote] = React.useState(false);

  const handleMouseHover = () => {
    if(showQuote === true){
      setShowQuote(false)
    }else{
      setShowQuote(true)
    }
  }

  return (
    <>
      <div className="section section-tabs" id="product-list-elements">
        <Container>
          <h2 className="title">Our Products</h2>
          <div id="product-list">
            <Row>
              <Col lg="3" sm="6" className="product-list-item">
                <div className="box-product-item">
                  <img
                    alt="..."
                    className="rounded img-raised img-product"
                    src={require("assets/img/no-image.png")}
                    onMouseEnter={() => handleMouseHover()}
                    onMouseLeave={() => handleMouseHover()}
                  ></img>
                  <h4>Beras</h4>
                  <Button className="btn-round btn-get-quote-item" color="info" outline type="button">
                    Get Quote
                  </Button>
                </div>
              </Col>
              <Col lg="3" sm="6" className="product-list-item">
                <div className="box-product-item">
                  <img
                    alt="..."
                    className="rounded img-raised img-product"
                    src={require("assets/img/no-image.png")}
                    onMouseEnter={() => handleMouseHover()}
                    onMouseLeave={() => handleMouseHover()}
                  ></img>
                  <h4>Gula</h4>
                  {showQuote && (
                    <Button className="btn-round btn-get-quote-item" color="info" outline type="button">
                      Get Quote
                    </Button>
                  )}
                </div>
              </Col>
              <Col lg="3" sm="6" className="product-list-item">
                <div className="box-product-item">
                  <img
                    alt="..."
                    className="rounded img-raised img-product"
                    src={require("assets/img/no-image.png")}
                    onMouseEnter={() => handleMouseHover()}
                    onMouseLeave={() => handleMouseHover()}
                  ></img>
                  <h4>Minyak Goreng</h4>
                  <Button className="btn-round btn-get-quote-item" color="info" outline type="button">
                    Get Quote
                  </Button>
                </div>
              </Col>
              <Col lg="3" sm="6" className="product-list-item">
                <div className="box-product-item">
                  <img
                    alt="..."
                    className="rounded img-raised img-product"
                    src={require("assets/img/no-image.png")}
                    onMouseEnter={() => handleMouseHover()}
                    onMouseLeave={() => handleMouseHover()}
                  ></img>
                  <h4>Tepung Terigu</h4>
                  <Button className="btn-round btn-get-quote-item" color="info" outline type="button">
                    Get Quote
                  </Button>
                </div>
              </Col>
            </Row>
          </div>
        </Container>
      </div>
    </>
  );
}

export default ProductListElements;
