import React from "react";

// reactstrap components
import {
  Button
} from "reactstrap";

// core components

function IndexProductItem() {
    const [showQuote, setShowQuote] = React.useState(false);

    const handleMouseHover = () => {
        if(showQuote === true){
        setShowQuote(false)
        }else{
        setShowQuote(true)
        }
    }

  return (
    <>
        <div className="box-product-item">
            <img
                alt="..."
                className="rounded img-raised img-product"
                src={require("assets/img/no-image.png")}
                onMouseEnter={() => handleMouseHover()}
                onMouseLeave={() => handleMouseHover()}
                ></img>
            <h4>Beras</h4>
            {showQuote && (
                <Button className="btn-round btn-get-quote-item" color="info" outline type="button">
                    Get Quote
                </Button>
            )}
        </div>
    </>
  );
}

export default IndexProductItem;
