import React from "react";
import PropTypes from "prop-types";

// reactstrap components
import {
    Row,
    Col,
    Button
} from "reactstrap";

// core components

const IndexProductItemDetail = props => {
    const { title, description, image} = props;

  return (
    <>
        <div className="box-product-item-detail">
            <Row>
                <Col lg="6" sm="12">
                    <div className="product-detail-image">
                        <img
                            alt={title}
                            className="rounded img-raised img-product"
                            src={image}>
                        </img>
                    </div>
                    <div className="product-detail-text">
                        <h3>{title}</h3>
                        <p>{description}</p>
                        <Button 
                            className="btn-round btn-get-quote-item" 
                            color="info" 
                            outline type="button">
                                Get Quote
                        </Button>
                    </div>
                </Col>
                <Col lg="6" sm="12"></Col>
            </Row>
        </div>
    </>
  );
}

IndexProductItemDetail.propTypes = {
    title: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
	image: PropTypes.string.isRequired
};

export default IndexProductItemDetail;
