import React from "react";
import PropTypes from "prop-types";

// reactstrap components
import {
    Row,
    Col,
    Button,
    Modal,
    ModalBody
} from "reactstrap";

// core components

const IndexProductItemDetail = props => {
    const { title, description, image} = props;
    const [modal, setModal] = React.useState(false);

  return (
    <>
        <div className="box-product-item-detail">
            <Row>
                <Col lg="6" sm="12">
                    <div className="product-detail-text">
                        <h3>{title}</h3>
                        <p>{description}</p>
                        <Button 
                            className="btn-round btn-get-quote-product-detail" 
                            color="info" 
                            outline type="button"
                            onClick={() => setModal(true)}>
                                Get Quote
                        </Button>
                    </div>
                </Col>
                <Col lg="6" sm="12">
                    <div className="product-detail-image">
                        <img
                            alt={title}
                            className="rounded img-raised img-product"
                            src={image}>
                        </img>
                    </div>
                </Col>
            </Row>
        </div>
        <Modal
            modalClassName="modal modal-info"
            toggle={() => setModal(false)}
            isOpen={modal}
        >
            <div className="modal-header justify-content-center">
                <button
                    className="close"
                    type="button"
                    onClick={() => setModal(false)}
                >
                    <i className="now-ui-icons ui-1_simple-remove"></i>
                </button>
                    <h4 className="title title-up">Modal title</h4>
            </div>
            <ModalBody>
                <p>Always have an access to your profile</p>
            </ModalBody>
            <div className="modal-footer">
            </div>
        </Modal>
    </>
  );
}

IndexProductItemDetail.propTypes = {
    title: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
	image: PropTypes.string.isRequired
};

export default IndexProductItemDetail;
